<?phpinclude("inc.aplication_top.php");$ajax = new Ajax(NULL, $cuenta);/* DESCRIPCION AVENTURA */if(isset($_POST["updateFace"]) && $_POST["updateFace"] != "" && $_POST["likes"] != "" && $_POST["comments"] != ""){    if($_POST["type"] == "coments" || $_POST["type"] == "likes" || $_POST["type"] == "all"){        Aventuras::update_likes_comments($_POST["id"], $_POST["type"], $_POST["likes"], $_POST["comments"]);    }}if (isset($_POST["favoritos"])) {    $ajax->agregar_favoritos($_POST["id"]);}/* EVENTOS */if (isset($_POST["mod_evento"])) {    $ajax->asistencia_evento($_POST["mod_evento"], $_POST["id"]);}if (isset($_POST["idCbo"])) {    $ajax->llenarCboModalidades($_POST["idCbo"]);}if (isset($_POST["deportes"])) {    $ajax->llenarCboDeportes();}if (isset($_POST["agencias"])) {    $idDeporte = (int) $_POST["idDeporte"];    if ($idDeporte > 0) {        $ajax->llenarCboAgencias($idDeporte);    }}if (isset($_POST["step"])) {    if ($_POST["step"] == 1 && $_POST["titulo"] != "" && $_POST["descripcion"] != "" && $_POST["files"] != "") {        $ajax->comparteAventura_step2($_POST["cbo_deportes"], $_POST["cbo_modalidad"], $_POST["titulo"], $_POST["lugar"], $_POST["descripcion"], $_POST["files"], $_POST["videos"]);        $_SESSION["step1"] = 'step1';    } else if ($_POST["step"] == 2) {        $_SESSION["step2"] = 'step2';    }}if (isset($_POST["nombre_image"])) {    $ajax->eliminar_archivo_xnombre($_POST["nombre_image"]);}/* MODIFICAR UNA AVENTURA */if (isset($_POST["imagenMd"])) {    $ajax->guardarTemporal_img($_POST["img"]);}if (isset($_POST["videoMd"])) {    $ajax->guardarTemporal_videos($_POST["video"]);}/* Eliminar una imagen */if (isset($_POST["id_image"])) {    $ajax->eliminar_archivo_aventura($_POST["id_image"]);}if (isset($_POST["src_image"])) {    $ajax->eliminar_archivo_xnombre($_POST["src_image"]);}/* Ordenar posicion de imagenes */if (isset($_POST["list_post"])) {    $ajax->ordenarArchivos($_POST["order"]);}/* ELIMINAR AVENTURA */if (isset($_POST["elim_av"])) {    $aventura = new Aventuras();    $aventura->deleteAventura($_POST["id"]);}if (isset($_POST["elim_favorito"])) {    $favorito = new Favoritos();    $favorito->deleteFavoritos($_POST["id"]);}/* ---------------- * -----MORE CONTENT---- * ---------------- */if (isset($_POST["action"]) && $_POST["action"] == "loadMore" && $_POST["id"] != "" && $_POST["type"] != "") {    $aventura = new Aventuras();    if ($_POST["type"] === "all" || $_POST["type"] === "deporte" || $_POST["type"] === "modalidad" || $_POST["type"] === "cliente") {        echo $aventura->load_more_content($_POST["id"], $_POST["type"], $_POST["uid"]);    } else {        echo 0;    }}?>